import { useContext, useEffect, useRef } from "react";
import { CartContext } from "../pages/ProductPage";
import { IconX } from "@tabler/icons-react";

function CartItem() {
  const { cartItem, setCartItem } = useContext(CartContext);
  const renderedItemsRef = useRef({}); // Track rendered items

  useEffect(() => {
    // After each render, update the ref with the current cart state
    cartItem.forEach(item => {
      renderedItemsRef.current[item.id] = true;
    });
  }, [cartItem]);

  const increase = (id) => {
    setCartItem((prevCartItems) =>
      prevCartItems.map((item) =>
        item.id === id ? { ...item, boughtQty: item.boughtQty + 1 } : item
      )
    );
  };

  const decrease = (id) => {
    setCartItem((prevCartItems) =>
      prevCartItems.map((item) =>
        item.id === id && item.boughtQty > 1
          ? { ...item, boughtQty: item.boughtQty - 1 }
          : item
      )
    );
  };

  const calcPrice = (quantity, itemPrice) => {
    return quantity * itemPrice;
  };

  const removeFromCart = (id) => {
    const updatedCart = cartItem.filter((item) => item.id !== id);
    setCartItem(updatedCart);
    localStorage.setItem("cartItem", JSON.stringify(updatedCart));
  };

  return (
    <>
      {cartItem.map((item) => {
        // Check if the item has been rendered before
        if (!renderedItemsRef.current[item.id]) {
          return (
            <div key={item.id} className="cart-item">
              <div className="cart-img">
                <img src={item.img} alt="product" />
              </div>
              <div className="cart-middle">
                <p className="cart-name">{item.description}</p>
                <div className="cart-btns">
                  <button onClick={() => decrease(item.id)}>-</button>
                  <p className="quantity">{item.boughtQty}</p>
                  <button onClick={() => increase(item.id)}>+</button>
                </div>
              </div>
              <div className="cart-right">
                <p className="cart-price">Â¥{calcPrice(item.boughtQty, item.price)}</p>
                <IconX onClick={() => removeFromCart(item.id)} />
              </div>
            </div>
          );
        } else {
          return null;
        }
      })}
    </>
  );
}

export default CartItem;
