import { useContext, useEffect } from "react";
import { CartContext, QtyCartContext } from "../pages/ProductPage";
import { IconX } from "@tabler/icons-react";

function CartItem() {
  const { cartItem, setCartItem } = useContext(CartContext);
  const { qtyCartItem, setQtyCartItem } = useContext(QtyCartContext);

  // Function to get the quantity for a specific item
  const getQuantityForItem = (itemId) => {
    const foundItem = qtyCartItem.find((item) => item.id === itemId);
    return foundItem ? foundItem.quantity : 0;
  };

  // Increase quantity for a specific item
  const increase = (itemId) => {
    setQtyCartItem((prevQtyCartItem) =>
      prevQtyCartItem.map((item) =>
        item.id === itemId ? { ...item, quantity: item.quantity + 1 } : item
      )
    );
  };

  // Decrease quantity for a specific item
  const decrease = (itemId) => {
    setQtyCartItem((prevQtyCartItem) =>
      prevQtyCartItem.map((item) =>
        item.id === itemId && item.quantity > 1
          ? { ...item, quantity: item.quantity - 1 }
          : item
      )
    );
  };

  // Calculate price based on quantity and price
  const calcPrice = (qty, price) => {
    return qty * price;
  };

  // Remove an item from the cart
  const removeFromCart = (id) => {
    const updateCart = cartItem.filter((item) => item.id !== id);
    setCartItem(updateCart);
    const updateQtyCart = qtyCartItem.filter((item) => item.id !== id);
    setQtyCartItem(updateQtyCart);

    // Update localStorage
    localStorage.setItem("cartItem", JSON.stringify(updateCart));
    localStorage.setItem("qtyCartItem", JSON.stringify(updateQtyCart));
  };

  // Sync local storage on mount
  useEffect(() => {
    const storedCartItem = JSON.parse(localStorage.getItem("cartItem")) || [];
    const storedQtyCartItem = JSON.parse(localStorage.getItem("qtyCartItem")) || [];
    setCartItem(storedCartItem);
    setQtyCartItem(storedQtyCartItem);
  }, [setCartItem, setQtyCartItem]);

  return (
    <>
      {cartItem.map((item, id) => (
        <div key={id} className="cart-item">
          <div className="cart-img">
            <img src={item.img} alt="product" />
          </div>
          <div className="cart-middle">
            <p className="cart-name">{item.description}</p>
            <div className="cart-btns">
              <button onClick={() => decrease(item.id)}>-</button>
              <p className="qtyCartItem">{getQuantityForItem(item.id)}</p>
              <button onClick={() => increase(item.id)}>+</button>
            </div>
          </div>
          <div className="cart-right">
            <p className="cart-price">Â¥{calcPrice(getQuantityForItem(item.id), item.price)}</p>
            <IconX onClick={() => removeFromCart(item.id)} />
          </div>
        </div>
      ))}
    </>
  );
}

export default CartItem;
