import { useContext } from "react";
import { CartContext } from "../pages/ProductPage";
import { IconX } from "@tabler/icons-react";

function CartItem() {
  const { cartItem, setCartItem } = useContext(CartContext);

  const increase = (id) => {
    setCartItem((prevCartItems) =>
      prevCartItems.map((item) =>
        item.id === id ? { ...item, boughtQty: item.boughtQty + 1 } : item
      )
    );
  };

  const decrease = (id) => {
    setCartItem((prevCartItems) =>
      prevCartItems.map((item) =>
        item.id === id && item.boughtQty > 1
          ? { ...item, boughtQty: item.boughtQty - 1 }
          : item
      )
    );
  };

  const calcPrice = (quantity, itemPrice) => {
    return quantity * itemPrice;
  };

  const removeFromCart = (id) => {
    const updatedCart = cartItem.filter((item) => item.id !== id);
    setCartItem(updatedCart);
    localStorage.setItem("cartItem", JSON.stringify(updatedCart)); // Update local storage
  };

  return (
    <>
      {cartItem.map((item) => (
        <div key={item.id} className="cart-item">
          Hello WOrld
        </div>
      ))}
    </>
  );
}

export default CartItem;
